<script>
// import {mapState, mapGetters, mapMutations} from 'vuex'
import { mapState, mapActions } from 'vuex'
import { debounce } from '~/helpers/index'

export default {
  name: 'filter.vue',
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  //                    MACHT "programs" ALS DATEN IN DIESER KOMPONENTE VERFÜGBAR. MAPT ALSO VON DEM ZUSTAND AUF DIE DATEN DER KOMPONENTE.
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  //                    IN "index.js" WURDEN "programs" BEREITS VON DER DATENBANK GEHOLT.
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  computed: {
    ...mapState(['programs', 'filter', 'filteredPrograms', 'lengthOfFilteredPrograms']),
    ...mapState('prepopulate', ['prepopulateData', 'country'])
  },
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  //                    MACHT "getprograms" IN DIESER KOMPONENTE VERFÜGBAR UND MACHT FILTER HANDLE-BAR
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  //                    - SPREAD OPERATOR ... SORGT FÜR DAS MERGEN MIT BEREITS BESTEHENDEN METHODEN.
  /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// /// ///
  //                    - FILTER-HANDLER: WENDET MIT ZWEI PARAMETERN EINEN DISPATCH AN
  //                      - MIT STATUS (WAS IM FELD AUSGEFÜLLT WURDE)
  //                      - MIT DER ACTION AUS index.js
  //                        - 1) DIE ACTION WIEDERUM IMPORTIERT COMMIT UND FÜHRT SELBIGEN AUS, UM DEN FILTER IM STATE ZU ÄNDERN
  //                          - EINE MUTATION (AUCH IN index.js) IST TEIL DES COMMIT. SIE ENTSCHEIDET, WAS IM STATE VERÄNDERT WIRD
  //                        - 2) UND SIE IMPORTIERT DISPATCH, MIT DEM filterPrograms AUS index.js ANGESTOßEN WIRD.
  //                          - DIESE WIEDERUM HOLT SICH AKTUELLE PROGRAMS UND FILTER UND RUFT DAMIT filterPrograms AUS filters.js AUF.
  //                          - DAMIT WERDEN DIE PROGRAMS ENTSPRECHEND DEM FILTER GEFILTERT UND IM STATE GEÄNDERT
  methods: {
    truncate (str, n) {
      return (str.length > n) ? str.substr(0, n - 1) + '&hellip;' : str
    },
    saveDetailProgram (program) {
      this.$store.dispatch('setDetailProgram', program)
    },
    handleCountryFilter (status) {
      this.$store.dispatch('filterStatus', status)
    },
    handleThemeFilter (status) {
      this.$store.dispatch('filterTheme', status)
    },
    handleTypeFilter (status) {
      this.$store.dispatch('filterType', status)
    },
    handleSearch: debounce(function (e) {
      this.$store.dispatch('filterSearch', e.target.value)
    }, 500),
    handleFilterOrder (orderBy) {
      this.orderOpen = false
      this.orderChanged = true
      this.$store.dispatch('filterOrder', orderBy)
    },
    closeOrderDropDown (e) {
      this.orderOpen = false
    },
    // Methode mit Namespace "programs"
    ...mapActions(['getPrograms'])
  },
  filters: {
    capitalize (value) {
      if (!value) {
        return ''
      }
      value = value.toString()
      return value.charAt(0).toUpperCase() + value.slice(1)
    },
    truncate (string, value) {
      return (string || '').substring(0, value) + '…'
    }
  }
}
</script>

